mapper.update:
UPDATE ORDER
ORDER_DESC = #{orderDesc}, ORDER_DELIVERY_DATE = #{orderDeliveryDate}, ORDER_DELIVERY_ADDRESS = #{orderDeliveryAddress}, ORDER_CONTACT_NAME = #{orderContactName}, ORDER_CONTACT_PHONE = #{orderContactPhone}, ORDER_TOTAL_ESTIMATION = #{orderTotalEstimation}, ORDER_STATUS = #{orderStatus}, ORDER_EFFECTIVE_BEGIN_DATE = #{orderEffectiveBeginDate}, ORDER_EFFECTIVE_END_DATE = #{orderEffectiveEndDate}, ORDER_BID_RECORD_LIMIT = #{orderBidRecordLimit}, ORDER_FINAL_BID_UID = #{orderFinalBidUid}, ORDER_FINAL_BID_PRICE = #{orderFinalBidPrice}, ORDER_COMMENT = #{orderComment}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ORDER_CODE = #{orderCode} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ORDER_CODE AS "orderCode",
  ORDER_DESC AS "orderDesc",
  ORDER_DELIVERY_DATE AS "orderDeliveryDate",
  ORDER_DELIVERY_ADDRESS AS "orderDeliveryAddress",
  ORDER_CONTACT_NAME AS "orderContactName",
  ORDER_CONTACT_PHONE AS "orderContactPhone",
  ORDER_TOTAL_ESTIMATION AS "orderTotalEstimation",
  ORDER_STATUS AS "orderStatus",
  ORDER_EFFECTIVE_BEGIN_DATE AS "orderEffectiveBeginDate",
  ORDER_EFFECTIVE_END_DATE AS "orderEffectiveEndDate",
  ORDER_BID_RECORD_LIMIT AS "orderBidRecordLimit",
  ORDER_FINAL_BID_UID AS "orderFinalBidUid",
  ORDER_FINAL_BID_PRICE AS "orderFinalBidPrice",
  ORDER_COMMENT AS "orderComment",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ORDER
WHERE 1 = 1 
  AND ORDER_CODE = #{orderCode}
  AND ORDER_DESC = #{orderDesc}
  AND ORDER_DELIVERY_DATE = #{orderDeliveryDate}
  AND ORDER_DELIVERY_ADDRESS = #{orderDeliveryAddress}
  AND ORDER_CONTACT_NAME = #{orderContactName}
  AND ORDER_CONTACT_PHONE = #{orderContactPhone}
  AND ORDER_TOTAL_ESTIMATION = #{orderTotalEstimation}
  AND ORDER_STATUS = #{orderStatus}
  AND ORDER_EFFECTIVE_BEGIN_DATE = #{orderEffectiveBeginDate}
  AND ORDER_EFFECTIVE_END_DATE = #{orderEffectiveEndDate}
  AND ORDER_BID_RECORD_LIMIT = #{orderBidRecordLimit}
  AND ORDER_FINAL_BID_UID = #{orderFinalBidUid}
  AND ORDER_FINAL_BID_PRICE = #{orderFinalBidPrice}
  AND ORDER_COMMENT = #{orderComment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ORDER
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ORDER_CODE = #{orderCode}
  AND ORDER_DESC = #{orderDesc}
  AND ORDER_DELIVERY_DATE = #{orderDeliveryDate}
  AND ORDER_DELIVERY_ADDRESS = #{orderDeliveryAddress}
  AND ORDER_CONTACT_NAME = #{orderContactName}
  AND ORDER_CONTACT_PHONE = #{orderContactPhone}
  AND ORDER_TOTAL_ESTIMATION = #{orderTotalEstimation}
  AND ORDER_STATUS = #{orderStatus}
  AND ORDER_EFFECTIVE_BEGIN_DATE = #{orderEffectiveBeginDate}
  AND ORDER_EFFECTIVE_END_DATE = #{orderEffectiveEndDate}
  AND ORDER_BID_RECORD_LIMIT = #{orderBidRecordLimit}
  AND ORDER_FINAL_BID_UID = #{orderFinalBidUid}
  AND ORDER_FINAL_BID_PRICE = #{orderFinalBidPrice}
  AND ORDER_COMMENT = #{orderComment}
