mapper.update:
UPDATE ORDER_ITEM_PROP
ORDER_ITEM_CODE = #{orderItemCode}, ORDER_ITEM_PROP_GROUP = #{orderItemPropGroup}, ORDER_ITEM_PROP_KEY = #{orderItemPropKey}, ORDER_ITEM_PROP_VALUE = #{orderItemPropValue}, ORDER_ITEM_PROP_OPTION = #{orderItemPropOption}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ORDER_CODE = #{orderCode} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ORDER_CODE AS "orderCode",
  ORDER_ITEM_CODE AS "orderItemCode",
  ORDER_ITEM_PROP_GROUP AS "orderItemPropGroup",
  ORDER_ITEM_PROP_KEY AS "orderItemPropKey",
  ORDER_ITEM_PROP_VALUE AS "orderItemPropValue",
  ORDER_ITEM_PROP_OPTION AS "orderItemPropOption",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ORDER_ITEM_PROP
WHERE 1 = 1 
  AND ORDER_CODE = #{orderCode}
  AND ORDER_ITEM_CODE = #{orderItemCode}
  AND ORDER_ITEM_PROP_GROUP = #{orderItemPropGroup}
  AND ORDER_ITEM_PROP_KEY = #{orderItemPropKey}
  AND ORDER_ITEM_PROP_VALUE = #{orderItemPropValue}
  AND ORDER_ITEM_PROP_OPTION = #{orderItemPropOption}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ORDER_ITEM_PROP
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ORDER_CODE = #{orderCode}
  AND ORDER_ITEM_CODE = #{orderItemCode}
  AND ORDER_ITEM_PROP_GROUP = #{orderItemPropGroup}
  AND ORDER_ITEM_PROP_KEY = #{orderItemPropKey}
  AND ORDER_ITEM_PROP_VALUE = #{orderItemPropValue}
  AND ORDER_ITEM_PROP_OPTION = #{orderItemPropOption}
