mapper.update:
UPDATE ORDER_BID
BID_DESC = #{bidDesc}, BID_PRICE = #{bidPrice}, BID_CONTACT_NAME = #{bidContactName}, BID_CONTACT_PHONE = #{bidContactPhone}, BID_COMMENT = #{bidComment}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ORDER_CODE = #{orderCode} AND BID_UID = #{bidUid} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ORDER_CODE AS "orderCode",
  BID_UID AS "bidUid",
  BID_DESC AS "bidDesc",
  BID_PRICE AS "bidPrice",
  BID_CONTACT_NAME AS "bidContactName",
  BID_CONTACT_PHONE AS "bidContactPhone",
  BID_COMMENT AS "bidComment",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ORDER_BID
WHERE 1 = 1 
  AND ORDER_CODE = #{orderCode}
  AND BID_UID = #{bidUid}
  AND BID_DESC = #{bidDesc}
  AND BID_PRICE = #{bidPrice}
  AND BID_CONTACT_NAME = #{bidContactName}
  AND BID_CONTACT_PHONE = #{bidContactPhone}
  AND BID_COMMENT = #{bidComment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ORDER_BID
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ORDER_CODE = #{orderCode}
  AND BID_UID = #{bidUid}
  AND BID_DESC = #{bidDesc}
  AND BID_PRICE = #{bidPrice}
  AND BID_CONTACT_NAME = #{bidContactName}
  AND BID_CONTACT_PHONE = #{bidContactPhone}
  AND BID_COMMENT = #{bidComment}
