mapper.update:
UPDATE ORDER_ITEM
ORDER_ITEM_DESC = #{orderItemDesc}, ORDER_ITEM_SPECS = #{orderItemSpecs}, ORDER_ITEM_QUANTITY = #{orderItemQuantity}, ORDER_ITEM_ESTIMATION = #{orderItemEstimation}, ORDER_ITEM_LOGISTICS_CODE = #{orderItemLogisticsCode}, ORDER_ITEM_LOGISTICS_DESC = #{orderItemLogisticsDesc}, ORDER_ITEM_ORDER_STATUS = #{orderItemOrderStatus}, ORDER_ITEM_COMMENT = #{orderItemComment}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE ORDER_CODE = #{orderCode} AND ORDER_ITEM_CODE = #{orderItemCode} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  ORDER_CODE AS "orderCode",
  ORDER_ITEM_CODE AS "orderItemCode",
  ORDER_ITEM_DESC AS "orderItemDesc",
  ORDER_ITEM_SPECS AS "orderItemSpecs",
  ORDER_ITEM_QUANTITY AS "orderItemQuantity",
  ORDER_ITEM_ESTIMATION AS "orderItemEstimation",
  ORDER_ITEM_LOGISTICS_CODE AS "orderItemLogisticsCode",
  ORDER_ITEM_LOGISTICS_DESC AS "orderItemLogisticsDesc",
  ORDER_ITEM_ORDER_STATUS AS "orderItemOrderStatus",
  ORDER_ITEM_COMMENT AS "orderItemComment",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM ORDER_ITEM
WHERE 1 = 1 
  AND ORDER_CODE = #{orderCode}
  AND ORDER_ITEM_CODE = #{orderItemCode}
  AND ORDER_ITEM_DESC = #{orderItemDesc}
  AND ORDER_ITEM_SPECS = #{orderItemSpecs}
  AND ORDER_ITEM_QUANTITY = #{orderItemQuantity}
  AND ORDER_ITEM_ESTIMATION = #{orderItemEstimation}
  AND ORDER_ITEM_LOGISTICS_CODE = #{orderItemLogisticsCode}
  AND ORDER_ITEM_LOGISTICS_DESC = #{orderItemLogisticsDesc}
  AND ORDER_ITEM_ORDER_STATUS = #{orderItemOrderStatus}
  AND ORDER_ITEM_COMMENT = #{orderItemComment}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM ORDER_ITEM
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND ORDER_CODE = #{orderCode}
  AND ORDER_ITEM_CODE = #{orderItemCode}
  AND ORDER_ITEM_DESC = #{orderItemDesc}
  AND ORDER_ITEM_SPECS = #{orderItemSpecs}
  AND ORDER_ITEM_QUANTITY = #{orderItemQuantity}
  AND ORDER_ITEM_ESTIMATION = #{orderItemEstimation}
  AND ORDER_ITEM_LOGISTICS_CODE = #{orderItemLogisticsCode}
  AND ORDER_ITEM_LOGISTICS_DESC = #{orderItemLogisticsDesc}
  AND ORDER_ITEM_ORDER_STATUS = #{orderItemOrderStatus}
  AND ORDER_ITEM_COMMENT = #{orderItemComment}
