mapper.update:
UPDATE REQ_BID
BID_DESC = #{bidDesc}, BID_PRICE = #{bidPrice}, BID_OWNER = #{bidOwner}, BID_OWNER_MOBILE = #{bidOwnerMobile}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE REQ_CODE = #{reqCode} AND BID_CODE = #{bidCode} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  REQ_CODE AS "reqCode",
  BID_CODE AS "bidCode",
  BID_DESC AS "bidDesc",
  BID_PRICE AS "bidPrice",
  BID_OWNER AS "bidOwner",
  BID_OWNER_MOBILE AS "bidOwnerMobile",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM REQ_BID
WHERE 1 = 1 
  AND REQ_CODE = #{reqCode}
  AND BID_CODE = #{bidCode}
  AND BID_DESC = #{bidDesc}
  AND BID_PRICE = #{bidPrice}
  AND BID_OWNER = #{bidOwner}
  AND BID_OWNER_MOBILE = #{bidOwnerMobile}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM REQ_BID
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND REQ_CODE = #{reqCode}
  AND BID_CODE = #{bidCode}
  AND BID_DESC = #{bidDesc}
  AND BID_PRICE = #{bidPrice}
  AND BID_OWNER = #{bidOwner}
  AND BID_OWNER_MOBILE = #{bidOwnerMobile}
