mapper.update:
UPDATE GXT_USER
USER_POST_ADDR = #{userPostAddr}, USER_POST_ADDR_OTHER = #{userPostAddrOther}, USER_BANK_ACC_NO = #{userBankAccNo}, USER_BANK_ACC_NO_OTHER = #{userBankAccNoOther}, USER_BANK_ACC_NAME = #{userBankAccName}, USER_BANK_ACC_NAME_OTHER = #{userBankAccNameOther}, USER_IDENTITY_CARD = #{userIdentityCard}, WECHAT_OPENID = #{wechatOpenid}, WECHAT_CITY = #{wechatCity}, WECHAT_COUNTRY = #{wechatCountry}, WECHAT_HEADIMGURL = #{wechatHeadimgurl}, WECHAT_LANGUAGE = #{wechatLanguage}, WECHAT_NICKNAME = #{wechatNickname}, WECHAT_PROVINCE = #{wechatProvince}, WECHAT_SEX = #{wechatSex}, WECHAT_UNIONID = #{wechatUnionid}, WECHAT_PRIVILEGE = #{wechatPrivilege}, WECHAT_GROUPID = #{wechatGroupid}, WECHAT_TAGID_LIST = #{wechatTagidList}, WECHAT_SUBSCRIBE = #{wechatSubscribe}, WECHAT_SUBSCRIBE_TIME = #{wechatSubscribeTime}, CREATE_AUTHOR = #{createAuthor}, CREATE_DATETIME = #{createDatetime}, UPDATE_AUTHOR = #{updateAuthor}, UPDATE_DATETIME = current_timestamp()
WHERE USER_ID = #{userId} AND UPDATE_DATETIME = #{updateDatetime}

mapper.select:
SELECT
  USER_ID AS "userId",
  USER_POST_ADDR AS "userPostAddr",
  USER_POST_ADDR_OTHER AS "userPostAddrOther",
  USER_BANK_ACC_NO AS "userBankAccNo",
  USER_BANK_ACC_NO_OTHER AS "userBankAccNoOther",
  USER_BANK_ACC_NAME AS "userBankAccName",
  USER_BANK_ACC_NAME_OTHER AS "userBankAccNameOther",
  USER_IDENTITY_CARD AS "userIdentityCard",
  WECHAT_OPENID AS "wechatOpenid",
  WECHAT_CITY AS "wechatCity",
  WECHAT_COUNTRY AS "wechatCountry",
  WECHAT_HEADIMGURL AS "wechatHeadimgurl",
  WECHAT_LANGUAGE AS "wechatLanguage",
  WECHAT_NICKNAME AS "wechatNickname",
  WECHAT_PROVINCE AS "wechatProvince",
  WECHAT_SEX AS "wechatSex",
  WECHAT_UNIONID AS "wechatUnionid",
  WECHAT_PRIVILEGE AS "wechatPrivilege",
  WECHAT_GROUPID AS "wechatGroupid",
  WECHAT_TAGID_LIST AS "wechatTagidList",
  WECHAT_SUBSCRIBE AS "wechatSubscribe",
  WECHAT_SUBSCRIBE_TIME AS "wechatSubscribeTime",
  CREATE_AUTHOR AS "createAuthor",
  CREATE_DATETIME AS "createDatetime",
  UPDATE_AUTHOR AS "updateAuthor",
  UPDATE_DATETIME AS "updateDatetime"
FROM GXT_USER
WHERE 1 = 1 
  AND USER_ID = #{userId}
  AND USER_POST_ADDR = #{userPostAddr}
  AND USER_POST_ADDR_OTHER = #{userPostAddrOther}
  AND USER_BANK_ACC_NO = #{userBankAccNo}
  AND USER_BANK_ACC_NO_OTHER = #{userBankAccNoOther}
  AND USER_BANK_ACC_NAME = #{userBankAccName}
  AND USER_BANK_ACC_NAME_OTHER = #{userBankAccNameOther}
  AND USER_IDENTITY_CARD = #{userIdentityCard}
  AND WECHAT_OPENID = #{wechatOpenid}
  AND WECHAT_CITY = #{wechatCity}
  AND WECHAT_COUNTRY = #{wechatCountry}
  AND WECHAT_HEADIMGURL = #{wechatHeadimgurl}
  AND WECHAT_LANGUAGE = #{wechatLanguage}
  AND WECHAT_NICKNAME = #{wechatNickname}
  AND WECHAT_PROVINCE = #{wechatProvince}
  AND WECHAT_SEX = #{wechatSex}
  AND WECHAT_UNIONID = #{wechatUnionid}
  AND WECHAT_PRIVILEGE = #{wechatPrivilege}
  AND WECHAT_GROUPID = #{wechatGroupid}
  AND WECHAT_TAGID_LIST = #{wechatTagidList}
  AND WECHAT_SUBSCRIBE = #{wechatSubscribe}
  AND WECHAT_SUBSCRIBE_TIME = #{wechatSubscribeTime}
  AND CREATE_AUTHOR = #{createAuthor}
  AND UPDATE_AUTHOR = #{updateAuthor}
  AND CREATE_DATETIME > #{createDatetime}
  AND UPDATE_DATETIME > #{updateDatetime}
 ORDER BY ${theOrderByCondition}
 GROUP BY ${theGroupByCondition}
LIMIT ${theFetchStart} , ${theFetchSize}

SELECT count(*)
FROM GXT_USER
WHERE 1 = 1 
   AND ( ${theSQLCondition} )
  AND USER_ID = #{userId}
  AND USER_POST_ADDR = #{userPostAddr}
  AND USER_POST_ADDR_OTHER = #{userPostAddrOther}
  AND USER_BANK_ACC_NO = #{userBankAccNo}
  AND USER_BANK_ACC_NO_OTHER = #{userBankAccNoOther}
  AND USER_BANK_ACC_NAME = #{userBankAccName}
  AND USER_BANK_ACC_NAME_OTHER = #{userBankAccNameOther}
  AND USER_IDENTITY_CARD = #{userIdentityCard}
  AND WECHAT_OPENID = #{wechatOpenid}
  AND WECHAT_CITY = #{wechatCity}
  AND WECHAT_COUNTRY = #{wechatCountry}
  AND WECHAT_HEADIMGURL = #{wechatHeadimgurl}
  AND WECHAT_LANGUAGE = #{wechatLanguage}
  AND WECHAT_NICKNAME = #{wechatNickname}
  AND WECHAT_PROVINCE = #{wechatProvince}
  AND WECHAT_SEX = #{wechatSex}
  AND WECHAT_UNIONID = #{wechatUnionid}
  AND WECHAT_PRIVILEGE = #{wechatPrivilege}
  AND WECHAT_GROUPID = #{wechatGroupid}
  AND WECHAT_TAGID_LIST = #{wechatTagidList}
  AND WECHAT_SUBSCRIBE = #{wechatSubscribe}
  AND WECHAT_SUBSCRIBE_TIME = #{wechatSubscribeTime}
